const animItems=document.querySelectorAll(".anim-item");if(animItems.length){function animateElems(){for(let e=0;e<animItems.length;e++){const t=animItems[e],n=t.offsetHeight,o=getCoords(t).top;let s=window.innerHeight-n/4;n>window.innerHeight&&(s=window.innerHeight-window.innerHeight/4),pageYOffset>o-s&&pageYOffset<o+n?t.classList.contains("show")||t.classList.add("show"):t.classList.contains("no-hide")||t.classList.remove("show")}}function getCoords(e){const t=e.getBoundingClientRect();return scrollLeft=window.pageXOffset||document.documentElement.scrollLeft,scrollTop=window.pageYOffset||document.documentElement.scrollTop,{top:t.top+scrollTop,left:t.left+scrollLeft}}window.addEventListener("scroll",animateElems),setTimeout(()=>{animateElems()},400)}